pragma solidity ^0.5.0;

contract Toasts {
  uint storedData;

  struct UserData {
    string name;
    string discription;
    uint balance;
  }

  struct Reputation {
    mapping(address => uint) addressToStakeIndex;
    address[] stake_for;
    uint[] stake_amount;
  }

  struct Comment {
    address author;
    uint time_stamp;
    string article_url;
    string comment;
    uint staked;
    bool is_good;
  }

  mapping(address => UserData) usersData;

  Reputation[] reputations;
  mapping( address => uint) public addressToReputation;

  Comment[] public comments;
  mapping( address => uint[]) public addressToComments;



  mapping( string => mapping(address => string[])) recommends;

  function set(uint x) public {
    storedData = x;
  }

  function get() public view returns (uint) {
    return storedData;
  }

  function signUp(string memory _name,string memory _discription) public {
    usersData[msg.sender] = UserData(_name,_discription,0);
  }

  function stakeCoin(address to) public returns (uint) {
    //usersData[to].balance += msg.value;
    uint rep_id = addressToReputation[msg.sender];
    reputations[rep_id].stake_for.push(to);
    //reputations[rep_id].stake_amount.push(msg.value);
    return 10;
  }

  function getUserData(address user) public view returns (string memory,string memory,uint) {
    return (usersData[user].name,usersData[user].discription,usersData[user].balance);
  }

  function getReputations(address user) public view returns (address[] memory,uint[] memory) {
    uint rep_id = addressToReputation[user];
    return (reputations[rep_id].stake_for,reputations[rep_id].stake_amount);
  }





}
